INSERT INTO `locales_source` VALUES ('2779','','default','Drupal can make a block with the most recent news items of a feed. You can <a href="@block-admin">configure blocks</a> to be displayed in the sidebar of your page. This setting lets you configure the number of news items to show in a feed''s block. If you choose ''0'' these feeds'' blocks will be disabled.','','7.22'),('2780','','default','You must <em>either</em> upload a file or enter a URL.','','none'),('2781','','default','This URL is not valid.','','none'),('2782','','default','No new feed has been added.','','none'),('2783','','default','The URL %url is invalid.','','none'),('2784','','default','A feed named %title already exists.','','none'),('2785','','default','A feed with the URL %url already exists.','','none'),('2786','','default','Fetchers download data from an external source. Choose a fetcher suitable for the external source you would like to download from.','','none'),('2787','','default','Parsers transform downloaded data into standard structures. Choose a parser suitable for the type of feeds you would like to aggregate.','','none'),('2788','','default','Processors act on parsed feed data, for example they store feed items. Choose the processors suitable for your task.','','none'),('2789','','default','For most aggregation tasks, the default settings are fine.','','none'),('2790','','default','A category named %category already exists. Enter a unique title.','','none'),('2791','','default','Default fetcher','','7.22'),('2792','','default','Downloads data from a URL using Drupal''s HTTP request handler.','','7.22'),('2793','','default','Default parser','','7.22'),('2794','','default','Parses RSS, Atom and RDF feeds.','','7.22'),('2795','','default','Default processor','','7.22'),('2796','','default','Creates lightweight records from feed items.','','7.22'),('2797','','default','Default processor settings','','none'),('2798','','default','Number of items shown in listing pages','','7.22'),('27