INSERT INTO `locales_source` VALUES ('3030','','default','These settings apply to the %field field everywhere it is used. These settings impact the way that data is stored in the database and cannot be changed once data has been created.','','7.41'),('3031','','default','%field has no field settings.','','7.41'),('3032','','default','Updated field %label field settings.','','7.41'),('3033','','default','Attempt to update field %label failed: %message.','','none'),('3034','','default','Change widget','','7.41'),('3035','','default','The type of form element you would like to present to the user when creating this field in the %type type.','','7.41'),('3036','','default','Changed the widget for field %label.','','7.36'),('3037','','default','There was a problem changing the widget for field %label.','','none'),('3038','','default','This field is <strong>locked</strong> and cannot be deleted.','','none'),('3039','','default','The field %field has been deleted from the %type content type.','','7.41'),('3040','','default','There was a problem removing the %field from the %type content type.','','none'),('3041','','default','These settings apply only to the %field field when used in the %type type.','','7.41'),('3042','','default','Required field','','7.41'),('3043','','default','These settings apply to the %field field everywhere it is used. Because the field already has data, some settings can no longer be changed.','','7.41'),('3044','','default','These settings apply to the %field field everywhere it is used.','','7.41'),('3045','','default','%field field settings','','7.41'),('3046','','default','The default value for this field, used when creating new content.','','7.41'),('3047','','default','Saved %label configuration.','','7.41'),('3048','','default','This list shows all fields currently in use for easy reference.','','none'),('3049','','default','Save and add fields','','7.41'),('3050','','default','Field list','','7.41'),('3051','','default','Manage display',''