INSERT INTO `locales_source` VALUES ('3322','','default','If selected and this menu link has children, the menu will always appear expanded.','','7.41'),('3323','','default','Parent link','','7.41'),('3324','','default','The maximum depth for a link and all its children is fixed at !maxdepth. Some menu links may not be available as parents if selecting them would exceed this limit.','','7.41'),('3325','','default','Optional. In the menu, the heavier links will sink and the lighter links will be positioned nearer the top.','','7.41'),('3326','','default','Are you sure you want to delete the custom menu link %item?','','7.41'),('3327','','default','The menu link %title has been deleted.','','7.41'),('3328','','default','Are you sure you want to reset the link %item to its default values?','','7.41'),('3329','','default','The menu link was reset to its default settings.','','7.41'),('3330','','default','No Main links','','7.23'),('3331','','default','Source for the Main links','','7.23'),('3332','','default','Select what should be displayed as the Main links (typically at the top of the page).','','7.23'),('3333','','default','No Secondary links','','7.23'),('3334','','default','Source for the Secondary links','','7.23'),('3335','','default','Select the source for the Secondary links. An advanced option allows you to use the same source for both Main links (currently %main) and Secondary links: if your source menu has two levels of hierarchy, the top level menu links will appear in the Main links, and the children of the active link will appear in the Secondary links.','','7.23'),('3336','','default','Deleted custom menu %title and all its menu links.','','none'),('3337','','default','Deleted menu link %title.','','none'),('3338','','default','<strong>Warning:</strong> There is currently 1 menu link in %title. It will be deleted (system-defined items will be reset).','','7.22'),('3339','','default','<strong>Warning:</strong> There are currently @count menu links in %ti