INSERT INTO `locales_source` VALUES ('3341','','default','Users with the <em>Administer menus and menu items</em> permission can add, edit and delete custom menus on the <a href="@menu">Menus administration page</a>. Custom menus can be special site menus, menus of external links, or any combination of internal and external links. You may create an unlimited number of additional menus, each of which will automatically have an associated block. By selecting <em>list links</em>, you can add, edit, or delete links for a given menu. The links listing page provides a drag-and-drop interface for controlling the order of links, and creating a hierarchy within the menu.','','7.41'),('3342','','default','Displaying menus','','7.41'),('3343','','default','After you have created a menu, you must enable and position the associated block on the <a href="@blocks">Blocks administration page</a>.','','7.41'),('3344','','default','You can enable the newly-created block for this menu on the <a href="@blocks">Blocks administration page</a>.','','7.22'),('3345','','default','Each menu has a corresponding block that is managed on the <a href="@blocks">Blocks administration page</a>.','','7.41'),('3346','','default','Administer menus and menu items','','7.41'),('3347','','default','Provide a menu link','','7.41'),('3348','','default','Menu links with smaller weights are displayed before links with larger weights.','','7.41'),('3349','','default','Available menus','','7.41'),('3350','','default','The menus available to place links in for this content type.','','7.41'),('3351','','default','Default parent item','','7.41'),('3352','','default','Choose the menu item to be the default parent for a new link in the content authoring form.','','7.41'),('3353','','default','Add new menus to your site, edit existing menus, and rename and reorganize menu links.','','7.41'),('3354','','default','Parent menu items','','7.41'),('3355','','default','Edit menu link','','7.41'),('3356','','default','Reset menu link','','7.41'),('3357','','default','Delete menu link','','7.41'),('3358','','default','The <em>Navigation</em> menu contains links intended for site visitors. Links are added to the <em>Navigation</em> menu automatically by some modules.','','none');
INSERT INTO `locales_source` VALUES ('3359','','default','The <em>User</em> menu contains links related to the user''s account, as well as the ''Log out'' link.','','none'),('3360','','default','The <em>Management</em> menu contains links for administrative tasks.','','none'),('3361','','default','The <em>Main</em> menu is used on many sites to show the major sections of the site, often in a top navigation bar.','','none'),('3362','','default','manage display','','7.41'),('3363','','default','No content types available. <a href="@link">Add content type</a>.','','7.41'),('3364','','default','The human-readable name of this content type. This text will be displayed as part of the list on the <em>Add new content</em> page. It is recommended that this name begin with a capital letter and contain only letters, numbers, and spaces. This name must be unique.','','7.41'),('3365','','default','Describe this content type. The text will be displayed on the <em>Add new content</em> page.','','7.41'),('3366','','default','Preview before submitting','','7.41'),('3367','','default','This text will be displayed at the top of the page when creating or editing content of this type.','','7.41'),('3368','','default','Users with the <em>Administer content</em> permission will be able to override these options.','','7.41'),('3369','','default','Display author and date information.','','7.41'),('3370','','default','Author username and publish date will be displayed.','','7.41'),('3371','','default','Invalid machine-readable name. Enter a name other than %invalid.','','none'),('3372','','default','%type is used by 1 piece of content on your site. If you remove this content type, you will not be able to edit the %type content and it may not display correctly.','','none'),('3373','','default','%type is used by @count pieces of content on your site. If you remove %type, you will not be able to edit the %type content and it may not display correctly.','','none'),('3374','','default','Publish selected content','','7.41'),('3375','','default','Unpublish selected content','','7.41');
INSERT INTO `locales_source` VALUES ('3376','','default','Promote selected content to front page','','7.41'),('3377','','default','Demote selected content from front page','','7.41'),('3378','','default','Make selected content sticky','','7.41'),('3379','','default','Make selected content not sticky','','7.41'),('3380','','default','Delete selected content','','7.41'),('3381','','default','Deleted @count posts.','','7.41'),('3382','','default','Are you sure you want to delete this item?','','none'),('3383','','default','Are you sure you want to delete these items?','','7.41'),('3384','','default','<em>Edit @type</em> @title','','7.41'),('3385','','default','You have not created any content types yet. Go to the <a href="@create-content">content type creation page</a> to add a new content type.','','none'),('3386','','default','Revision log message','','7.41'),('3387','','default','Provide an explanation of the changes you are making. This will help other authors understand your motivations.','','7.41'),('3388','','default','Tokens related to individual content items, or "nodes".','','7.41'),('3389','','default','The unique ID of the content item, or "node".','','7.41'),('3390','','default','The main body text of the node.','','7.41'),('3391','','default','The summary of the node''s main body text.','','7.41'),('3392','','default','The content access permissions need to be rebuilt. <a href="@node_access_rebuild">Rebuild permissions</a>.','','none'),('3393','','default','The Node module manages the creation, editing, deletion, settings, and display of the main site content. Content items managed by the Node module are typically displayed as pages on your site, and include a title, some meta-data (author, creation time, content type, etc.), and optional fields containing text or other data (fields are managed by the <a href="@field">Field module</a>). For more information, see the online handbook entry for <a href="@node">Node module</a>.','','7.41'),('3394','','default','Creating content','','7.41'),('3395','','default','When new content is created, the Node module records basic information about the content, including the author, date of creation, and the <a href="@content-type">Content type</a>. It also manages the <em>publishing options</em>, which define whether or not the content is published, promoted to the front page of the site, and/or sticky at the top of content lists. Default settings can be configured for each <a href="@content-type">type of content</a> on your site.','','7.41');
INSERT INTO `locales_source` VALUES ('3396','','default','Creating custom content types','','7.41'),('3397','','default','The Node module gives users with the <em>Administer content types</em> permission the ability to <a href="@content-new">create new content types</a> in addition to the default ones already configured. Creating custom content types allows you the flexibility to add <a href="@field">fields</a> and configure default settings that suit the differing needs of various site content.','','7.41'),('3398','','default','Administering content','','7.41'),('3399','','default','The <a href="@content">Content administration page</a> allows you to review and bulk manage your site content.','','7.41'),('3400','','default','Creating revisions','','7.41'),('3401','','default','The Node module also enables you to create multiple versions of any content, and revert to older versions using the <em>Revision information</em> settings.','','7.41'),('3402','','default','User permissions','','7.41'),('3403','','default','The Node module makes a number of permissions available for each content type, which can be set by role on the <a href="@permissions">permissions page</a>.','','7.41'),('3404','','default','Individual content types can have different fields, behaviors, and permissions assigned to them.','','7.41'),('3405','','default','Content items can be displayed using different view modes: Teaser, Full content, Print, RSS, etc. <em>Teaser</em> is a short format that is typically used in lists of multiple content items. <em>Full content</em> is typically used when the content is displayed on its own page.','','7.41'),('3406','','default','Here, you can define which fields are shown and hidden when %type content is displayed in each view mode, and define how the fields are displayed in each view mode.','','7.41'),('3407','','default','Revisions allow you to track differences between multiple versions of your content, and revert back to older versions.','','7.22'),('3408','','default','Full content','','7.41'),('3409','','default','Search result','','7.32');
INSERT INTO `locales_source` VALUES ('3410','','default','The content on this page has either been modified by another user, or you have already submitted modifications using this form. As a result, your changes cannot be saved.','','7.23'),('3411','','default','Administer content types','','7.41'),('3412','','default','Warning: Give to trusted roles only; this permission has security implications.','','7.41'),('3413','','default','View published content','','7.41'),('3414','','default','Access the content overview page','','7.41'),('3415','','default','Bypass content access control','','7.41'),('3416','','default','View content revisions','','7.41'),('3417','','default','Revert content revisions','','7.41'),('3418','','default','Delete content revisions','','7.41'),('3419','','default','View own unpublished content','','7.41'),('3420','','default','Content is sticky at top of lists','','7.22'),('3421','','default','Content is promoted to the front page','','7.22'),('3422','','default','Recent content','','7.41'),('3423','','default','Number of recent content items to display','','none'),('3424','','default','Show block for specific content types','','7.41'),('3425','','default','Show this block only on pages that display content of the given type(s). If you select no types, there will be no type-specific limitation.','','7.41'),('3426','','default','Welcome to @site-name','','7.41'),('3427','','default','No front page content has been created yet.','','7.41'),('3428','','default','Unpublish content','','7.41'),('3429','','default','Make content sticky','','7.41'),('3430','','default','Make content unsticky','','7.41'),('3431','','default','Promote content to front page','','7.41'),('3432','','default','Remove content from front page','','7.41'),('3433','','default','Change the author of content','','7.41'),('3434','','default','Save content','','7.41'),('3435','','default','Unpublish content containing keyword(s)','','7.41'),('3436','','default','The content will be unpublished if it contains any of the phrases above. Use a case-sensitive, comma-separated list of phrases. Example: funny, bungee jumping, "Company, Inc."','','none');
INSERT INTO `locales_source` VALUES ('3437','','default','If the site is experiencing problems with permissions to content, you may have to rebuild the permissions cache. Rebuilding will remove all privileges to content and replace them with permissions based on the current modules and settings. Rebuilding may take some time if there is a lot of content or complex permission settings. After rebuilding has completed, content will automatically use the new permissions.','','7.41'),('3438','','default','Node Access Permissions','','7.41'),('3439','','default','One permission in use','','none'),('3440','','default','@count permissions in use','','none'),('3441','','default','Find and manage content.','','none'),('3442','','default','Manage content types, including default status, front page promotion, comment settings, etc.','','7.41'),('3443','','default','Edit content type','','none'),('3444','; modules/node/content_types.js','default','Don''t display post information','','none'),('3445','','default','This site supports <a href="@openid-net">OpenID</a>, a secure way to log in to many websites using a single username and password. OpenID can reduce the necessity of managing many usernames and passwords for many websites.','','none'),('3446','','default','If you already have an OpenID, enter the URL to your OpenID server below (e.g. myusername.openidprovider.com). Next time you log in, you will be able to use this URL instead of a regular username and password. You can have multiple OpenID servers if you like; just keep adding them here.','','none'),('3447','','default','The OpenID module allows users to log in using the OpenID single sign on service. <a href="@openid-net">OpenID</a> is a secure method for logging into many websites with a single username and password. It does not require special software, and it does not share passwords with any site to which it is associated, including the site being logged into. The main benefit to users is that they can have a single password that they can use on many websites. This means they can easily update their single password from a centralized location, rather than having to change dozens of passwords individually. For more information, see the online handbook entry for <a href="@handbook">OpenID module</a>.','','none');
INSERT INTO `locales_source` VALUES ('3448','','default','Logging in with OpenID','','none'),('3449','','default','To log in using OpenID, a user must already have an OpenID account. Users can then create site accounts using their OpenID, assign one or more OpenIDs to an existing account, and log in using an OpenID. This lowers the barrier to registration, which helps increase the user base, and offers convenience and security to the users. Because OpenID cannot guarantee a user is legitimate, email verification is still necessary. When logging in, users are presented with the option of entering their OpenID URL, which will look like <em>myusername.openidprovider.com</em>. The site then communicates with the OpenID server, asking it to verify the identity of the user. If the user is logged into their OpenID server, the server communicates back to your site, verifying the user. If they are not logged in, the OpenID server will ask the user for their password. At no point does the site being logged into record the user''s OpenID password.','','none'),('3450','','default','Once you have verified your e-mail address, you may log in via OpenID.','','none'),('3451','','default','Sorry, that is not a valid OpenID. Ensure you have spelled your ID correctly.','','none'),('3452','','default','Complete the registration by filling out the form below. If you already have an account, you can <a href="@login">log in</a> now and add your OpenID under "My account".','','none'),('3453','','default','Account registration using the information provided by your OpenID provider failed due to the reasons listed below. Complete the registration by filling out the form below. If you already have an account, you can <a href="@login">log in</a> now and add your OpenID under "My account".','','none'),('3454','','default','The Overlay module makes the administration pages on your site display in a JavaScript overlay of the page you were viewing when you clicked the administrative link, instead of replacing the page in your browser window. Use the close link on the overlay to return to the page you were viewing when you clicked the link. For more information, see the online handbook entry for <a href="@overlay">Overlay module</a>.','','7.41');
INSERT INTO `locales_source` VALUES ('3455','','default','Access the administrative overlay','','7.41'),('3456','','default','View administrative pages in the overlay.','','7.41'),('3457','','default','Displays the Drupal administration interface in an overlay.','','7.41'),('3458','; modules/overlay/overlay-parent.js; sites/all/modules/jquery_update/replace/misc/1.9/overlay-parent.js','default','@title dialog','','none'),('3459','','default','Specify the existing path you wish to alias. For example: node/28, forum/1, taxonomy/term/1.','','none'),('3460','','default','The Path module allows you to specify an alias, or custom URL, for any existing internal system path. Aliases should not be confused with URL redirects, which allow you to forward a changed or inactive URL to a new URL. In addition to making URLs more readable, aliases also help search engines index content more effectively. Multiple aliases may be used for a single internal system path. To automate the aliasing of paths, you can install the contributed module <a href="@pathauto">Pathauto</a>. For more information, see the online handbook entry for the <a href="@path">Path module</a>.','','7.41'),('3461','','default','Creating aliases','','7.41'),('3462','','default','Users with sufficient <a href="@permissions">permissions</a> can create aliases under the <em>URL path settings</em> section when they create or edit content. Some examples of aliases are: ','','7.41'),('3463','','default','<em>member/jane-smith</em> aliased to internal path <em>user/123</em>','','7.41'),('3464','','default','<em>about-us/team</em> aliased to internal path <em>node/456</em>','','7.41'),('3465','','default','Managing aliases','','7.41'),('3466','','default','The Path module provides a way to search and view a <a href="@aliases">list of all aliases</a> that are in use on your website. Aliases can be added, edited and deleted through this list.','','7.41'),('3467','','default','An alias defines a different name for an existing URL path - for example, the alias ''about'' for the URL path ''node/1''. A URL path can have multiple aliases.','','7.36');
INSERT INTO `locales_source` VALUES ('3468','','default','Administer URL aliases','','7.41'),('3469','','default','Create and edit URL aliases','','7.41'),('3470','','default','The alias is already in use.','','7.22'),('3471','','default','Optionally specify an alternative URL by which this term can be accessed. Use a relative path and don''t add a trailing slash or the URL alias won''t work.','','7.41'),('3472','','default','Enabling execution of PHP in text fields','','none'),('3473','','default','The PHP filter module allows users with the proper permissions to include custom PHP code that will get executed when pages of your site are processed. While this is a powerful and flexible feature if used by a trusted user with PHP experience, it is a significant and dangerous security risk in the hands of a malicious or inexperienced user. Even a trusted user may accidentally compromise the site by entering malformed or incorrect PHP code. Only the most trusted users should be granted permission to use the PHP filter, and all PHP code added through the PHP filter should be carefully examined before use. <a href="@php-snippets">Example PHP snippets</a> can be found on Drupal.org.','','none'),('3474','','default','Use PHP for settings','','none'),('3475','','default','<p>Add a custom block to your site, named "Welcome" . With its text format set to "PHP code" (or another format supporting PHP input), add the following in the Block body:</p>\n<pre>\nprint t(''Welcome visitor! Thank you for visiting.'');\n</pre>','','none'),('3476','','default','<p>To display the name of a registered user, use this instead:</p>\n<pre>\nglobal $user;\nif ($user->uid) {\n  print t(''Welcome @name! Thank you for visiting.'', array(''@name'' => format_username($user)));\n}\nelse {\n  print t(''Welcome visitor! Thank you for visiting.'');\n}\n</pre>','','none'),('3477','','default','The PHP module has been disabled. Any existing content that was using the PHP filter will now be visible in plain text. This might pose a security risk by exposing sensitive information, if any, used in the PHP code.','','none');
INSERT INTO `locales_source` VALUES ('3478','','default','The Poll module can be used to create simple surveys or questionnaires that display cumulative results. A poll is a good way to receive feedback from site users and community members. For more information, see the online handbook entry for the <a href="@poll">Poll module</a>.','','none'),('3479','','default','Creating a poll','','none'),('3480','','default','Users can create a poll by clicking on Poll on the <a href="@add-content">Add new content</a> page, and entering the question being posed, the answer choices, and beginning vote counts for each choice. The status (closed or active) and duration (length of time the poll remains active for new votes) can also be specified.','','none'),('3481','','default','Viewing polls','','none'),('3482','','default','You can visit the <a href="@poll">Polls</a> page to view all current polls, or alternately enable the <em>Most recent poll</em> block on the <a href="@blocks">Blocks administration page</a>. To vote in or view the results of a specific poll, you can click on the poll itself.','','none'),('3483','','default','Vote on polls','','none'),('3484','','default','Cancel and change own votes','','none'),('3485','','default','View voting results','','none'),('3486','','default','Your vote was cancelled.','','none'),('3487','','default','To enable browsing this field by value, enter a title for the resulting page. The word <code>%value</code> will be substituted with the corresponding value. An example page title is "People whose favorite color is %value" . This is only applicable for a public field.','','none'),('3488','','default','To enable browsing this field by value, enter a title for the resulting page. An example page title is "People who are employed" . This is only applicable for a public field.','','none'),('3489','','default','The Profile module allows site administrators to define custom fields (such as country, full name, or age) for user profiles, which are then displayed in the <a href="@user">My Account</a> section. This permits users of a site to share more information about themselves, and can help community-based sites organize users around specific information. For more information, see the online handbook entry for <a href="@profile">Profile module</a>.','','none');
INSERT INTO `locales_source` VALUES ('3490','','default','Adding fields to the default profile','','none'),('3491','','default','To provide the ability for users to enter more information about themselves, the Profile module allows additional fields to be added to the default user profile. Examples of common additions would be <em>Location</em>, <em>Company</em>, <em>Age</em>, or <em>About me</em>.','','none'),('3492','','default','User information pages','','none'),('3493','','default','The Profile module enables links to see further information about site users. You can view both a main <a href="@profile">User list page</a>, and more specified pages by clicking on linked fields in any profile. For example, the <a href="@profile-country">People who live in Canada</a> listing on Drupal.org displays all users who have entered <em>Canada</em> in the <em>Country</em> field on their user profile.','','none'),('3494','','default','Author information block','','none'),('3495','','default','The <em>Author information block</em> is a default block created by the Profile module that can be enabled on the <a href="@blocks">Blocks administration page</a>. It shows visitors of your website information about the author of the page they are reading.','','none'),('3496','','default','This page displays a list of the existing custom profile fields to be displayed on a user''s <em>My account</em> page. To provide structure, similar or related fields may be placed inside a category. To add a new category (or edit an existing one), edit a profile field and provide a new category name.','','none'),('3497','','default','Enriches your content with metadata to let other applications (e.g. search engines, aggregators) better understand its relationships and attributes.','','7.41'),('3498','','default','Active search modules ','','none'),('3499','','default','The active search modules have been changed.','','none'),('3500','','default','You must include at least one positive keyword with 1 character or more.','','none'),('3501','','default','You must include at least one positive keyword with @count characters or more.','','none');
INSERT INTO `locales_source` VALUES ('3502','','default','Searched %type for %keys.','','none'),('3503','','default','The Search module provides the ability to index and search for content by exact keywords, and for users by username or e-mail. For more information, see the online handbook entry for <a href="@search-module">Search module</a>.','','7.41'),('3504','','default','Searching content and users','','7.41'),('3505','','default','Users with <em>Use search</em> permission can use the search block and <a href="@search">Search page</a>. Users with the <em>View published content</em> permission can search for content containing exact keywords. Users with the <em>View user profiles</em> permission can search for users containing the keyword anywhere in the user name, and users with the <em>Administer users</em> permission can search for users by email address. Additionally, users with <em>Use advanced search</em> permission can find content using more complex search methods and filtering by choosing the <em>Advanced search</em> option on the <a href="@search">Search page</a>.','','7.41'),('3506','','default','Indexing content with cron','','7.41'),('3507','','default','Configuring search settings','','7.41'),('3508','','default','Indexing behavior can be adjusted using the <a href="@searchsettings">Search settings page</a>. Users with <em>Administer search</em> permission can control settings such as the <em>Number of items to index per cron run</em>, <em>Indexing settings</em> (word length), <em>Active search modules</em>, and <em>Content ranking</em>, which lets you adjust the priority in which indexed content is returned in results.','','7.41'),('3509','','default','The Search module includes a default <em>Search form</em> block, which can be enabled and configured on the <a href="@blocks">Blocks administration page</a>. The block is available to users with the <em>Search content</em> permission.','','7.41'),('3510','','default','Extending Search module','','7.41'),('3511','','default','By default, the Search module only supports exact keyword matching in content searches. You can modify this behavior by installing a language-specific stemming module for your language (such as <a href="http://drupal.org/project/porterstemmer">Porter Stemmer</a> for American English), which allows words such as walk, walking, and walked to be matched in the Search module. Another approach is to use a third-party search technology with stemming or partial word matching features built in, such as <a href="http://drupal.org/project/apachesolr">Apache Solr</a> or <a href="http://drupal.org/project/sphinx">Sphinx</a>. These and other <a href="@contrib-search">search-related contributed modules</a> can be downloaded by visiting Drupal.org.','','7.41');
INSERT INTO `locales_source` VALUES ('3512','','default','<ul>\n<li>Check if your spelling is correct.</li>\n<li>Remove quotes around phrases to search for each word individually. <em>bike shed</em> will often show more results than <em>&quot;bike shed&quot;</em>.</li>\n<li>Consider loosening your query with <em>OR</em>. <em>bike OR shed</em> will often show more results than <em>bike shed</em>.</li>\n</ul>','','none'),('3513','','default','Administer search','','7.41'),('3514','','default','Use search','','7.41'),('3515','','default','Use advanced search','','7.41'),('3516','','default','Configure relevance settings for search and other indexing options.','','7.41'),('3517','','default','Choose a set of shortcuts to use','','7.22'),('3518','','default','Choose a set of shortcuts for this user','','7.22'),('3519','','default','You are now using the new %set_name shortcut set. You can edit it from this page or <a href="@switch-url">switch back to a different one.</a>','','none'),('3520','','default','%user is now using a new shortcut set called %set_name. You can edit it from this page.','','none'),('3521','','default','You are now using the %set_name shortcut set.','','none'),('3522','','default','%user is now using the %set_name shortcut set.','','none'),('3523','','default','Change set','','7.22'),('3524','','default','The shortcut set has been updated.','','none'),('3525','','default','Add new shortcut','','none'),('3526','','default','Editing @shortcut','','7.22'),('3527','','default','The name of the shortcut.','','7.22'),('3528','','default','The path to the shortcut.','','7.22'),('3529','','default','The link must correspond to a valid path on the site.','','none'),('3530','','default','The shortcut %link has been updated.','','none'),('3531','','default','Added a shortcut for %title.','','none'),('3532','','default','Are you sure you want to delete the shortcut %title?','','none'),('3533','','default','The shortcut %title has been deleted.','','none'),('3534