INSERT INTO `locales_source` VALUES ('3536','','default','Displaying shortcuts','','7.41'),('3537','','default','You can display your shortcuts by enabling the Shortcuts block on the <a href="@blocks">Blocks administration page</a>. Certain administrative modules also display your shortcuts; for example, the core <a href="@toolbar-help">Toolbar module</a> displays them near the top of the page, along with an <em>Edit shortcuts</em> link.','','7.41'),('3538','','default','Administer shortcuts','','7.41'),('3539','','default','@shortcut_set shortcuts','','none'),('3540','','default','Add to %shortcut_set shortcuts','','7.41'),('3541','','default','Add to shortcuts','','none'),('3542','','default','Remove from %shortcut_set shortcuts','','7.41'),('3543','','default','Remove from shortcuts','','none'),('3544','','default','Edit shortcuts','','7.41'),('3545','','default','Add shortcut','','7.22'),('3546','','default','Edit shortcut','','7.22'),('3547','','default','Delete shortcut','','none'),('3548','','default','Allows users to manage customizable lists of shortcut links.','','7.41'),('3549','','default','Value @value is TRUE.','','none'),('3550','','default','Value @value is FALSE.','','none'),('3551','','default','Value @value is NULL.','','none'),('3552','','default','Value @value is not NULL.','','none'),('3553','','default','Value @first is equal to value @second.','','none'),('3554','','default','Value @first is not equal to value @second.','','none'),('3555','','default','Value @first is identical to value @second.','','none'),('3556','','default','Value @first is not identical to value @second.','','none'),('3557','','default','The test did not complete due to a fatal error.','','none'),('3558','','default','Link with label %label found.','','none'),('3559','','default','Link with label %label not found.','','none'),('3560','','default','Link containing href %href found.','','none'),('3561','','default','No link containing href %href found.','','none'),('3562',