INSERT INTO `locales_source` VALUES ('4054','','default','Account details for [user:name] at [site:name] (pending admin approval)','','7.36'),('4055','','default','[user:name],\n\nThank you for registering at [site:name]. Your application for an account is currently pending approval. Once it has been approved, you will receive another e-mail containing information about how to log in, set your password, and other details.\n\n\n--  [site:name] team','','7.22'),('4056','','default','[user:name] has applied for an account.\n\n[user:edit-url]','','7.36'),('4057','','default','Replacement login information for [user:name] at [site:name]','','7.22'),('4058','','default','Account details for [user:name] at [site:name] (approved)','','7.22'),('4059','','default','Account details for [user:name] at [site:name] (blocked)','','7.22'),('4060','','default','Account cancellation request for [user:name] at [site:name]','','7.22'),('4061','','default','Account details for [user:name] at [site:name] (canceled)','','7.22'),('4062','','default','Cancel the selected user accounts','','7.41'),('4063','','default','The user account %name cannot be cancelled.','','none'),('4064','','default','When cancelling these accounts','','7.41'),('4065','','default','Are you sure you want to cancel these user accounts?','','7.41'),('4066','','default','Cancel accounts','','7.41'),('4067','','default','To make your password stronger:','','7.41'),('4068','','default','Make it at least 6 characters','','7.41'),('4069','','default','Add lowercase letters','','7.41'),('4070','','default','Add uppercase letters','','7.41'),('4071','','default','Add numbers','','7.41'),('4072','','default','Add punctuation','','7.41'),('4073','','default','Make it different from your username','','7.41'),('4074','','default','Weak','','7.41'),('4075','','default','Fair','','7.41'),('4076','','default','Blocked user: %name %email.','','none'),('4077','','default','There is currently 1 user online.','','7.23'),('4078','','de