INSERT INTO `locales_source` VALUES ('7133','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','API documentation','','7.41'),('7134','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','All configuration options described in the !apidocs that cannot be easily changed in the administration area can be set in the <strong>Advanced Options</strong> section in the CKEditor profile.','','7.41'),('7135','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Troubleshooting','','7.41'),('7136','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','the list of common problems','','7.41'),('7137','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Take a look at !listlink when installing CKEditor.','','7.41'),('7138','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','official project page','','7.41'),('7139','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','If you are looking for more information, have any trouble with the configuration, or found an issue with the CKEditor module, please visit the !officiallink.','','7.41'),('7140','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','here','','7.41'),('7141','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','More information about how to customize CKEditor for your theme can be found !herelink.','','7.41'),('7142','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Uploading images and files','','7.41'),('7143','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','There are three ways for uploading files:','','7.41'),('7144','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','CKFinder','','7.41'),('7145','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','By using !ckfinder (commercial), an advanced Ajax file manager.','','7.41'),('7146','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','defa