INSERT INTO `locales_source` VALUES ('7790','/index.php','default','Update user alias','','7.41'),('7791','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Provides a mechanism for modules to automatically generate aliases for the content they manage.','','7.41'),('7792','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Maximum alias and component length','','7.41'),('7793','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','The <strong>maximum alias length</strong> and <strong>maximum component length</strong> values default to 100 and have a limit of @max from Pathauto. This length is limited by the length of the "alias" column of the url_alias database table. The default database schema for this column is @max. If you set a length that is equal to that of the one set in the "alias" column it will cause problems in situations where the system needs to append additional words to the aliased URL. You should enter a value that is the length of the "alias" column minus the length of any strings that might get added to the end of the URL. The length of strings that might get added to the end of your URLs depends on which modules you have enabled and on your Pathauto settings. The recommended and default value is 100.','','7.41'),('7794','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Patterns','','7.41'),('7795','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Provides a user interface for the Token API and some missing core tokens.','','7.41'),('7796','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Bulk update','','7.41'),('7797','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','Delete aliases','','7.41'),('7798','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','pathauto','','7.41'),('7799','/Test/drupal7/Sauvages-PACA/admin/modules?render=overlay','default','token','','7.41'),('7800','/Test/drupal7/Sauvages