INSERT INTO `locales_target` VALUES ('2338','Cochez si vous voulez que le thème utilise son logo par défaut.','fr','0','0'),('2339','Chemin vers le logo que vous voulez utiliser à la place du logo par défaut.','fr','0','0'),('2340','Envoyer le logo','fr','0','0'),('2341','Paramètres de l''icône de raccourci','fr','0','0'),('2342','Votre icône de raccourci ou ''favicon'' est affichée dans la barre d''adresse et les favoris de la plupart des navigateurs.','fr','0','0'),('2343','Utiliser l''icône de raccourci par défaut.','fr','0','0'),('2344','Cocher ceci si vous souhaitez que le thème utilise l''icône de raccourci par défaut.','fr','0','0'),('2345','Le chemin du fichier image que vous souhaitez utiliser comme icône de raccourci personnalisée.','fr','0','0'),('2346','Transférer l''icône','fr','0','0'),('2347','Si vous n''avez pas un accès direct au serveur, utilisez ce champ pour transférer votre icône de raccourci.','fr','0','0'),('2348','@module (<span class="admin-missing">manquant</span>)','fr','0','0'),('2349','Les modules suivants vont être complètement désinstallés de votre site, et <em>toutes les données de ces modules seront perdues</em> !','fr','0','0'),('2350','Confirmer la désinstallation','fr','0','0'),('2351','Voulez-vous procéder à la désinstallation des éléments ci-dessus ?','fr','0','0'),('2352','Aucun module sélectionné.','fr','0','0'),('2353','Les modules sélectionnés ont été désinstallés.','fr','0','0'),('2354','Page 403 par défaut (accès refusé)','fr','0','0'),('2355','Page 404 par défaut (page non trouvée)','fr','0','0'),('2356','Les caches ont été vidés.','fr','0','0'),('2357','Sélectionnez une boîte à outils de traitement d''image','fr','0','0'),('2358','Nombre d''éléments pour chaque flux','fr','0','0'),('2359','Le nombre d''éléments par défaut à inclure dans chaque flux.','fr','0','0'),('2360','Contenu du flux','fr','0','0'),('2361','Titres et accroches','fr','0','0'),('2362','Paramètres globaux pour l''affichage, par défaut, du contenu de ch