INSERT INTO `locales_target` VALUES ('3989','Modifier les courriels de bienvenue envoyés aux nouveaux membres lors de leur inscription, lorsqu''une approbation administrative est requise.','fr','0','0'),('3990','Bienvenue (aucune approbation requise)','fr','0','0'),('3991','Modifier les courriels de bienvenue envoyés aux nouveaux membres lors de leur inscription, lorsqu''aucune approbation administrative n''est requise.','fr','0','0'),('3992','Récupération de mot de passe','fr','0','0'),('3993','Modifier les courriels envoyés aux utilisateurs demandant un nouveau mot de passe.','fr','0','0'),('3994','Activation du compte','fr','0','0'),('3995','Activer et modifier les courriels envoyés aux utilisateurs à l''activation de leur compte (lorsqu''un administrateur active le compte d''un utilisateur qui s''est inscrit sur un site où l''approbation administrative est requise).','fr','0','0'),('3996','Activer et modifier les courriels envoyés aux utilisateurs lorsque leur compte est bloqué.','fr','0','0'),('3997','Confirmation de l''annulation du compte','fr','0','0'),('3998','Modifier les courriels envoyés aux utilisateurs lorsqu''ils demandent l''annulation de leur compte.','fr','0','0'),('3999','Compte annulé','fr','0','0'),('4000','Activer et modifier les courriels envoyés aux utilisateurs lorsque leur compte est annulé.','fr','0','0'),('4001','Notifier l''utilisateur lorsque son compte est annulé.','fr','0','0'),('4002','Le nom de rôle %name existe déjà. Choisissez un autre nom de rôle.','fr','0','0'),('4003','Vous êtes connecté en tant que %user. <a href="!user_edit">Modifiez votre mot de passe.</a>','fr','0','0'),('4004','Un autre utilisateur (%other_user) est déjà connecté à ce site depuis cet ordinateur, mais vous avez essayé d''utiliser un lien unique pour l''utilisateur %resetting_user. Veuillez vous <a href="!logout">déconnecter</a> et essayer d''utiliser à nouveau ce lien.','fr','0','0'),('4005','Le lien de connexion unique sur lequel vous avez cliqué n''est pa